mapper.select:
SELECT c.id, c.status, c.car_number, c.car_type, c.car_model, c.car_load, d.name, d.telephone, c.create_time FROM ex_car c LEFT JOIN t_driver d ON c.driver_id = d.id WHERE 1 = 1
AND ( c.car_number LIKE '%' #{search} '%' OR c.car_type LIKE '%' #{search} '%' OR c.car_model LIKE '%' #{search} '%' OR d.name LIKE '%' #{search} '%' OR d.telephone LIKE '%' #{search} '%' )
ORDER BY ${page.sort} ${page.order}

mapper.update:
UPDATE 
	ex_car
SET 
	STATUS = #{status}
WHERE 
	id = #{carId}
