mapper.select:
SELECT * FROM t_staff WHERE 1=1
AND ( name LIKE '%' #{search} '%' OR telephone LIKE '%' #{search} '%' )
ORDER BY ${page.sort} ${page.order}

mapper.insert:
INSERT INTO t_staff (NAME, telephone, STATUS)
VALUES (
	#{staff.name},
	#{staff.telephone},
	#{staff.status}
)

mapper.update:
UPDATE
	t_staff
SET
	STATUS = #{status}
WHERE 
	id = #{staffId}
