mapper.select:
SELECT DISTINCT m.menu_id , parent_id , menu_name , href, icon FROM t_menu m LEFT JOIN t_role_menu r ON r.menu_id = m.menu_id WHERE STATUS =1 AND menu_type =#{menuType} AND r.role_id IN
#{roleId}
ORDER BY sort ASC

mapper.update:
UPDATE
t_menu
SET
STATUS = #{status}
WHERE menu_id =
#{menuId}
