mapper.select:
SELECT * FROM t_user_login_log WHERE user_id = #{userId}
AND ( user_ip LIKE '%' #{search} '%' OR operating_system LIKE '%' #{search} '%' OR browser LIKE '%' #{search} '%' )
ORDER BY ${page.sort} ${page.order}

mapper.update:
UPDATE
t_user
SET
PASSWORD =#{password}
WHERE u_id
=#{userId}

mapper.select:
SELECT u.u_id, u.organization_id, o.org_name, login_name, user_name, u.status, email, telephone, last_login_time, last_login_ip, u.create_time, u.create_by, u.update_by FROM t_user_role ur LEFT JOIN t_user u ON ur.user_id = u.u_id LEFT JOIN t_organization o ON u.organization_id = o.org_id WHERE ur.role_id=#{roleId}
AND ( user_name LIKE '%' #{search} '%' OR telephone LIKE '%' #{search} '%' OR email LIKE '%' #{search} '%' OR o.org_name LIKE '%' #{search} '%' )
ORDER BY ${page.sort} ${page.order}

mapper.update:
UPDATE
t_user
SET
STATUS = #{status}
WHERE u_id =
#{userId}
