mapper.select:
SELECT p.id, p.partition_code, p.addresskey, p.single, p.address, p.status, CONCAT(a.name, b.name, c.name) AS regionName FROM sys_partition p LEFT JOIN sys_area a ON a.`area_code` = p.province LEFT JOIN sys_area b ON b.`area_code` = p.city LEFT JOIN sys_area c ON c.`area_code` = p.village WHERE 1=1
AND ( p.partition_code LIKE '%' #{search} '%' OR p.address LIKE '%' #{search} '%' OR p.addresskey LIKE '%' #{search} '%' )
ORDER BY ${page.sort} ${page.order}

mapper.update:
UPDATE
sys_partition
SET
STATUS =#{status}
WHERE id =
#{partitionId}
